    setPokemonFilter({
      type: pokemonFilterVar().type,
      sortDescending,
    });
  } */

  return (
    <Collapse isOpen={show}>
      <Box
        p="20px"
        mt="4"
        border="2px"
        borderColor="red.500"
        rounded="md"
        shadow="md"
      >
        <TypeSelect />
        <HStack>
          <Box minW="125px" p={4}>
            <Text fontWeight="500" fontSize="md">
              Sort names:
            </Text>
          </Box>

          <Select
            bg="red.500"
            onChange={handleSortChange}
            value={pokemonSort}
            color="white"
            _focus={{
              color: "black",
            }}
          >
            <option value="false">Ascending</option>
            <option value="true">Descending</option>
          </Select>
        </HStack>
      </Box>
    </Collapse>
  );
};

export default SearchFilter;
