import { useLazyQuery, useQuery } from "@apollo/client";
import {
  Box,
  Button,
  Center,
  Collapse,
  Heading,
  HStack,
  NativeBaseProvider,
  Select,
  Text,
} from "native-base";
import React, { useState, ChangeEvent } from "react";
import { textStyle } from "styled-system";
import { pokemonFilterVar } from "../cache";
import pokemonTypes from "../constants/pokemonTypes";
import { GET_POKEMONS_LIMITED, GET_POKEMON_FILTER } from "../utils/queries";

interface Props {
  show: boolean;
  searchText: string;
}

const [getQuery, { data, loading, error, fetchMore }] = useLazyQuery(
  GET_POKEMONS_LIMITED,
  {
    variables: {
      name: searchText,
    },
  }
);

const SearchFilter = ({ show, searchText }: Props) => {
  // Get filter to initialize select value
  const { data: filterData } = useQuery(GET_POKEMON_FILTER);
  const [pokemonSort, setPokemonSort] = useState(
    filterData.pokemonFilter.sortDescending
  );

  const setFilter = () => {
    getQuery({
      variables: {
        sortDescending: pokemonSort,
        type: pokemonTypes,
      },
    });
  };

  /* function handleSortChange(e: ChangeEvent<HTMLSelectElement>) {
    const sortDescending = e.target.value === "true";
    setPokemonSort(e.target.value);
    setPokemonFilter({
      type: pokemonFilterVar().type,
      sortDescending,
    });
  } */

  return (
    <Collapse isOpen={show}>
      <Box
        p="10px"
        alignSelf="stretch"
        m={2}
        borderWidth={2}
        borderColor="red.500"
        rounded="md"
        shadow="md"
      >
        <HStack>
          <Box p={4}>
            <Text fontWeight="500" fontSize="md">
              Filter types:
            </Text>
          </Box>

          <Center>
            <Select
              defaultValue=""
              placeholder="Choose type"
              onValueChange={(pokemonSort) => setPokemonSort(pokemonSort)}
            >
              <Select.Item label="Show all" value="" />
              {pokemonTypes.map((type: string) => (
                <Select.Item label={type} key={type} value={type}>
                  {type}
                </Select.Item>
              ))}
            </Select>
          </Center>
        </HStack>
        <HStack>
          <Box p={4}>
            <Text fontWeight="500" fontSize="md">
              Sort names:
            </Text>
          </Box>

          <Center>
            <Select
              placeholder="Sort by"
              defaultValue="false"
              onValueChange={(sortDescending) => setPokemonSort(sortDescending)}
            >
              <Select.Item label="Ascending" value="false" />
              <Select.Item label="Descending" value="true" />
            </Select>
          </Center>
        </HStack>
        <Button onPress={setFilter}>Set filter</Button>
      </Box>
    </Collapse>
  );
};

export default SearchFilter;
